RSpec.describe LabgroupsController, type: :controller do
  # Labgroup. As you add validations to Labgroup, be sure to
  # adjust the attributes here as well.

  before :each do
    @request.env["devise.mapping"] = Devise.mappings[:user]
    @user = create(:user, role: User.roles[:patient])
    @client = create(:client)
    Sample.with_user(@user) do
      @sample = create(:sample, client: @client)
    end
    session[:labgroup] = @client.labgroup.id
    session[:lab] = @client.labgroup.labs.first.id
    sign_in @user
  end

  let(:valid_attributes) do
    { name: 'first group' }
  end

  let(:invalid_attributes) do
    { name: nil }
  end

  describe "GET /index" do
    it "renders a successful response" do
      Labgroup.create! valid_attributes
      get :index
      expect(response).to be_successful
    end
  end

  describe "GET /show" do
    it "renders a successful response" do
      labgroup = Labgroup.create! valid_attributes
      get :show, params: { id: labgroup.id }
      expect(response).to be_successful
    end
  end

  describe "GET /new" do
    it "renders a successful response" do
      get :new
      expect(response).to be_successful
    end
  end

  describe "GET /edit" do
    it "render a successful response" do
      labgroup = Labgroup.create! valid_attributes
      get :edit, params: { id: labgroup.id }
      expect(response).to be_successful
    end
  end

  describe "POST /create" do
    context "with valid parameters" do
      it "creates a new Labgroup" do
        expect do
          post :create, params: { labgroup: valid_attributes }
        end.to change(Labgroup, :count).by(1)
