      it "redirects to the created labgroup" do
        post :create, params: { labgroup: valid_attributes }
        expect(response).to redirect_to(labgroup_url(Labgroup.last))
      end
    end

    context "with invalid parameters" do
      it "does not create a new Labgroup" do
        expect do
          post :create, params: { labgroup: invalid_attributes }
        end.to change(Labgroup, :count).by(0)
      end

      it "renders a successful response (i.e. to display the 'new' template)" do
        post :create, params: { labgroup: invalid_attributes }
        expect(response).to be_successful
      end
    end
  end

  describe "PATCH /update" do
    context "with valid parameters" do
      let(:new_attributes) do
        { name: 'new name' }
      end

      it "updates the requested labgroup" do
        labgroup = Labgroup.create! valid_attributes
        patch :update, params: {id: labgroup.id, labgroup: new_attributes }
        labgroup.reload
        expect(labgroup.name).to eq 'new name'
      end

      it "redirects to the labgroup" do
